import { StyleSheet } from "react-native";

const styles = StyleSheet.create({
  resItemWrapper: {
    flexDirection: "column",
    alignItems: "center",
    padding: 10,
    marginTop: 20
  },
  itemImg: {
    height: 180,
    width: '100%',
    borderRadius: 20
  },
  heartIcon: {
    position: "absolute",
    top: 25,
    right: 20
  },
  resItemInforWrapper: {
    flexDirection: 'row',
    justifyContent: 'space-around'
  },
  basicInfWrapper: {
    flexDirection: 'column',
    alignItems: 'flex-start',
    marginTop: 10
  }
});

export default styles;
