/**
 * @example
 * ```js
 * const [executePromise, setExecutePromise] = useState();
 * const [ result, status ] = usePromise(()=>fetch("./any").then(res=>res.json()),executePromise);
 * ```
 * @param {()=>Promise<any>} promise -
 * @param {boolean} run
 * @param {any[]} [args]
 * @returns {State}
 */
export function usePromise(promise: () => Promise<any>, run: boolean, args?: any[] | undefined): State;
export type Status = {
    quiet: "";
    pending: "pending";
    fulfilled: "fulfilled";
    rejected: "rejected";
};
/**
 * @type {Status}
 */
export const Status: Status;
export type PromiseStatus = Status["quiet"] | Status["pending"] | Status["fulfilled"] | Status["rejected"];
export type State = [any, PromiseStatus];
