/**
 * Capture the click or touch event to execute the callback multiple times,
 * depending on the type of pressure
 * @param {import("atomico").Ref} ref
 * @param {() => any} callback
 * @param {number} [aceleration]
 * @param {number} [minMs]
 */
export function useClickPress(ref: import("atomico").Ref, callback: () => any, aceleration?: number | undefined, minMs?: number | undefined): void;
